import React, { useState, useEffect } from 'react';
import {
  Grid,
  Paper,
  Typography,
  Box,
  Card,
  CardContent,
} from '@mui/material';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

// Mock data - replace with real API calls
const mockEnergyData = {
  labels: Array.from({ length: 24 }, (_, i) => `${i}:00`),
  datasets: [
    {
      label: 'Energy Consumption (kWh)',
      data: Array.from({ length: 24 }, () => Math.random() * 2),
      borderColor: 'rgb(75, 192, 192)',
      tension: 0.1,
    },
  ],
};

const Dashboard: React.FC = () => {
  const [currentConsumption, setCurrentConsumption] = useState<number>(0);
  const [dailyTotal, setDailyTotal] = useState<number>(0);

  useEffect(() => {
    // Simulate real-time updates
    const interval = setInterval(() => {
      setCurrentConsumption(Math.random() * 2);
      setDailyTotal(prev => prev + Math.random() * 0.1);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Energy Dashboard
      </Typography>

      <Grid container spacing={3}>
        {/* Current Consumption */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Current Consumption
              </Typography>
              <Typography variant="h3">
                {currentConsumption.toFixed(2)} kWh
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Daily Total */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography color="textSecondary" gutterBottom>
                Daily Total
              </Typography>
              <Typography variant="h3">
                {dailyTotal.toFixed(2)} kWh
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Energy Consumption Chart */}
        <Grid item xs={12}>
          <Paper sx={{ p: 2 }}>
            <Typography variant="h6" gutterBottom>
              Hourly Energy Consumption
            </Typography>
            <Box sx={{ height: 400 }}>
              <Line
                data={mockEnergyData}
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  scales: {
                    y: {
                      beginAtZero: true,
                      title: {
                        display: true,
                        text: 'kWh',
                      },
                    },
                    x: {
                      title: {
                        display: true,
                        text: 'Hour',
                      },
                    },
                  },
                }}
              />
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Dashboard;
