import React, { useState } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  List,
  ListItem,
  ListItemText,
  ListItemIcon,
  Chip,
} from '@mui/material';
import {
  Tv as TvIcon,
  AcUnit as AcIcon,
  Kitchen as KitchenIcon,
  Lightbulb as LightIcon,
} from '@mui/icons-material';

// Mock device data
const mockDevices = [
  {
    id: 1,
    name: 'Living Room TV',
    type: 'Entertainment',
    consumption: 0.15,
    status: 'active',
    icon: TvIcon,
  },
  {
    id: 2,
    name: 'Air Conditioner',
    type: 'HVAC',
    consumption: 1.2,
    status: 'active',
    icon: AcIcon,
  },
  {
    id: 3,
    name: 'Refrigerator',
    type: 'Kitchen',
    consumption: 0.08,
    status: 'active',
    icon: KitchenIcon,
  },
  {
    id: 4,
    name: 'Living Room Lights',
    type: 'Lighting',
    consumption: 0.05,
    status: 'inactive',
    icon: LightIcon,
  },
];

const Devices: React.FC = () => {
  const [devices] = useState(mockDevices);

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Device Energy Consumption
      </Typography>

      <Grid container spacing={3}>
        {devices.map((device) => (
          <Grid item xs={12} sm={6} md={6} key={device.id}>
            <Card elevation={0}>
              <CardContent>
                <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
                  <device.icon sx={{ mr: 2, fontSize: 40, color: 'primary.main' }} />
                  <Box sx={{ flex: 1 }}>
                    <Typography variant="h6">{device.name}</Typography>
                    <Typography color="textSecondary">{device.type}</Typography>
                  </Box>
                  <Chip
                    label={device.status}
                    color={device.status === 'active' ? 'success' : 'default'}
                    size="small"
                  />
                </Box>
                <Typography variant="h4" color="primary">
                  {device.consumption.toFixed(2)} kWh
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Current consumption
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      <Box sx={{ mt: 4 }}>
        <Typography variant="h5" gutterBottom>
          Device Status Summary
        </Typography>
        <List>
          {devices.map((device) => (
            <ListItem key={device.id}>
              <ListItemIcon>
                <device.icon sx={{ color: 'info.main' }} />
              </ListItemIcon>
              <ListItemText
                primary={device.name}
                secondary={`${device.consumption.toFixed(2)} kWh - ${device.status}`}
              />
            </ListItem>
          ))}
        </List>
      </Box>
    </Box>
  );
};

export default Devices;
