import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Paper, Slider } from '@mui/material';

const Settings: React.FC = () => {
  const [threshold, setThreshold] = useState<number>(2.5);
  const [unit, setUnit] = useState<'kWh' | 'cost'>('kWh');

  const handleThresholdChange = (_: Event, value: number | number[]) => {
    setThreshold(Array.isArray(value) ? value[0] : value);
  };

  const handleUnitChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setUnit(e.target.value as 'kWh' | 'cost');
  };

  const handleSave = () => {
    // Save preferences (mock)
    alert(`Saved! Threshold: ${threshold} ${unit}`);
  };

  return (
    <Box>
      <Typography variant="h4" gutterBottom>
        Settings
      </Typography>
      <Paper sx={{ p: 4, maxWidth: 500 }} elevation={0}>
        <Typography variant="h6" gutterBottom>
          Alert Threshold (kWh)
        </Typography>
        <Slider
          value={threshold}
          min={0.5}
          max={10}
          step={0.1}
          onChange={handleThresholdChange}
          valueLabelDisplay="auto"
        />
        <TextField
          label="Preferred Unit"
          select
          SelectProps={{ native: true }}
          value={unit}
          onChange={handleUnitChange}
          fullWidth
          sx={{ mt: 3 }}
        >
          <option value="kWh">kWh</option>
          <option value="cost">Cost</option>
        </TextField>
        <Button variant="contained" color="primary" sx={{ mt: 3 }} onClick={handleSave}>
          Save Preferences
        </Button>
      </Paper>
    </Box>
  );
};

export default Settings;
